    if(smbm.hasClass('mm-open')){
      smbm.removeClass('mm-open');
      console.log('click');
    }else{
      smbm.addClass('mm-open');
    }
  });
  scls.click(function(){
    smbm.removeClass('mm-open');
  });
</script>
<script>
  let scrollpos = window.scrollY;
  const header = document.querySelector(".navbar");
  const header_height = header.offsetHeight;

  const add_class_on_scroll = () => header.classList.add("scrolled", "shadow-sm");
  const remove_class_on_scroll = () => header.classList.remove("scrolled", "shadow-sm");

  window.addEventListener('scroll', function () {
    scrollpos = window.scrollY;

    if (scrollpos >= header_height) { add_class_on_scroll(); }
    else { remove_class_on_scroll(); }

    console.log(scrollpos);
  })
</script>
<script type="text/javascript">
  var a = $('#langs');
  var b = $('#smbm');
window.addEventListener("load", function() {
$.ajax({
          url: "{{ route('langs') }}",
          type: "GET",
          headers: {
              'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
          },
          success: function(response) {
              var html = '';
              var mobhtml = '';
              $.each(response, function(index, item) {

                  if (item == 'en') {
                      
                      html += "<a href='{{ url('/lang') }}/" + item +
                          "' style='padding-top:6px;font-size:large;color:black;width:100%'>{{ __('website.en') }}<span class='langspan' style='display:inline-block;height:13px;background-image:" +
                          " url({{ asset('/imgs/en.png') }})" +
                          "'></span></a>";
                      mobhtml += "<li><a href='{{ url('/lang') }}/" + item +
                      "' style='padding-top:6px;font-size:large;color:white;width:100%'>{{ __('website.en') }}<span class='langspan' style='display:inline-block;height:13px;background-image:" +
                      " url({{ asset('/imgs/en.png') }})" +
                      "'></span></a></li>";
                  } else if (item == 'ar') {
                      html += "<a href='{{ url('/lang') }}/" + item +
                          "' style='padding-top:6px;font-size:x-large;color:black;width:100%'>{{ __('website.ar') }}<span class='langspan' style='display:inline-block;height:13px;background-image:" +
                          " url({{ asset('/imgs/ar.png') }})" +
                          "'></span></a>";
                      mobhtml += "<li><a href='{{ url('/lang') }}/" + item +
                      "' style='padding-top:6px;font-size:x-large;color:white;width:100%'>{{ __('website.ar') }}<span class='langspan' style='display:inline-block;height:13px;background-image:" +
                      " url({{ asset('/imgs/ar.png') }})" +
                      "'></span></a></li>";
                  } else if (item == 'jp') {
                      html += "<a href='{{ url('/lang') }}/" + item +
                          "' style='padding-top:6px;font-size:large;color:black;width:100%'>{{ __('website.jp') }}<span class='langspan' style='display:inline-block;height:13px;background-image: " +
                          "url({{ asset('/imgs/jp.png') }})" +
                          "'></span></a>";
                      mobhtml += "<li><a href='{{ url('/lang') }}/" + item +
                      "' style='padding-top:6px;font-size:large;color:white;width:100%'>{{ __('website.jp') }}<span class='langspan' style='display:inline-block;height:13px;background-image: " +
                      "url({{ asset('/imgs/jp.png') }})" +
                      "'></span></a></li>";
                  }

              });
              a.addClass('hidden');
              a.html(html);
              b.append(mobhtml);
          },
              error:function(e){
              console.log(e);
          }
      });
      $('#langb').click(function(e) {
              e.preventDefault();
              a.toggleClass("hidden");
                     
              });
          });
      </script>

